re (o.location_id =:location or parent_location =:location)&#xd;\n	AND o.concept_id = 2249 &#xd;\n	AND o.value_datetime BETWEEN :startDate AND :endDate&#xd;\n	AND MONTH(o.value_datetime) = MONTH(CAST(:endDate AS DATE))&#xd;\n    AND YEAR(o.value_datetime) =  YEAR(CAST(:endDate AS DATE))&#xd;\n	AND o.voided = 0&#xd;\n	&#xd;\nAND o.person_id not in (&#xd;\n   select distinct os.person_id &#xd;\n   from obs os&#xd;\n   where os.concept_id = 3634 AND os.value_coded = 2095 &#xd;\n   AND MONTH(obs_datetime) = MONTH(CAST(:endDate AS DATE))&#xd;\n   AND YEAR(obs_datetime) =  YEAR(CAST(:endDate AS DATE))&#xd;\n   )&#xd;\nAND o.person_id not in &#xd;\n	(&#xd;\n	select distinct person_id &#xd;\n						from person&#xd;\n						where death_date &lt;= :endDate&#xd;\n						and dead = 1&#xd;\n	)&#xd;\nAND o.person_id not in &#xd;\n	(&#xd;\n		select distinct os.person_id &#xd;\n				from obs os&#xd;\n				where (os.concept_id = 4155 and os.value_coded = 2146 and obs_datetime &lt;= :endDate and person_id not in &#xd;\n				(select person_id from&#xd;\n					(select person_id who, max(value_datetime) latest&#xd;\n					from obs &#xd;\n					where concept_ID = 2266&#xd;\n					and obs_datetime &lt;= :endDate&#xd;\n                    group by person_id) one,&#xd;\n					&#xd;\n					 (&#xd;\n					select person_id,obs_datetime &#xd;\n					from obs &#xd;\n					where concept_id = 3843&#xd;\n                    and obs_datetime &lt;= :endDate&#xd;\n					) two&#xd;\n					&#xd;\n					where who = person_id&#xd;\n					and DATE(obs_datetime) &gt; DATE(latest))&#xd;\n					)	&#xd;\n	)&#xd;\nAND person.voided = 0						 &#xd;\n;&#xd;\n</query>\n</org.openmrs.module.reporting.cohort.definition.SqlCohortDefinition>','2019-01-31 08:49:55',4,'2019-05-09 08:30:16',4,0,NULL,NULL,NULL,'6f37715f-711c-48fa-8f56-e3360233d885'),(341,'ART restart date SQL','','org.openmrs.module.reporting.cohort.definition.CohortDefinition','org.openmrs.module.reporting.cohort.definition.SqlCohortDefinition','org.openmrs.module.reporting