tion =:location)&#xd;\nand patient.patient_id not in  -- EXCLUDE CLIENTS NEWLY INITIATED ON ART&#xd;\n	                       (select distinct patient.patient_id AS Id&#xd;\n	                       from obs o&#xd;\n							INNER JOIN patient ON o.person_id = patient.patient_id													&#xd;\n							AND (o.concept_id = 2249 AND DATE(o.value_datetime) BETWEEN :startDate AND :endDate)&#xd;\n							AND patient.voided = 0 AND o.voided = 0&#xd;\n							and patient.patient_id not in(&#xd;\n														select distinct os.person_id from obs os															 &#xd;\n														where os.concept_id = 2396 														 &#xd;\n														AND DATE(os.obs_datetime) BETWEEN :startDate AND :endDate)&#xd;\n							&#xd;\n							)&#xd;\nAND o.person_id not in (&#xd;\n						-- EXCLUDE TRANSFER OUT WHO CAME BACK												&#xd;\n						select distinct os.person_id &#xd;\n				from obs os&#xd;\n				where (os.concept_id = 4155 and os.value_coded = 2146 and obs_datetime &lt; :endDate and person_id not in &#xd;\n				(select person_id from&#xd;\n					(select person_id who, max(value_datetime) latest&#xd;\n					from obs &#xd;\n					where concept_ID = 2266&#xd;\n                    and obs_datetime &lt;= :endDate&#xd;\n					group by person_id) one,&#xd;\n					&#xd;\n					(select person_id,obs_datetime &#xd;\n					from obs &#xd;\n					where concept_id = 3843 &#xd;\n                    and obs_datetime &lt;= :endDate) two&#xd;\n					&#xd;\n					where who = person_id&#xd;\n					and DATE(obs_datetime) &gt; DATE(latest))&#xd;\n					)													 &#xd;\n                        )&#xd;\n&#xd;\nAND patient.patient_id not in &#xd;\n			-- EXCLUDE DEAD&#xd;\n			(&#xd;\n			select distinct person_id &#xd;\n			from person&#xd;\n			where death_date &lt; :endDate&#xd;\n			and dead = 1&#xd;\n			)						&#xd;\n				&#xd;\n														</query>\n</org.openmrs.module.reporting.cohort.definition.SqlCohortDefinition>','2019-01-31 10:45:08',4,'2019-05-09 08:22:24',4,0,NULL,NULL,NULL,'092398d1-86b9-4486-97ff