rs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"8\">\n      <name>location</name>\n      <label>Location</label>\n      <type>org.openmrs.Location</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>460</id>\n  <query>SELECT Id&#xd;\nFROM ((SELECT Id, patientIdentifier AS &quot;Patient Identifier&quot;, patientName AS &quot;Patient Name&quot;, Age, Gender, age_group, HIV_Status, &apos;PITC&apos; AS &apos;HIV_Testing_Initiation&apos;&#xd;\n		, &apos;New&apos; AS &apos;Testing_History&apos; , sort_order&#xd;\nFROM&#xd;\n				(select distinct patient.patient_id AS Id,&#xd;\n									   patient_identifier.identifier AS patientIdentifier,&#xd;\n									   concat(person_name.given_name, &apos; &apos;, person_name.family_name) AS patientName,&#xd;\n									   floor(datediff(CAST(:endDate AS DATE), person.birthdate)/365) AS Age,&#xd;\n									   (select name from concept_name cn where cn.concept_id = o.value_coded and concept_name_type=&apos;FULLY_SPECIFIED&apos;) AS HIV_Status,&#xd;\n									   person.gender AS Gender,&#xd;\n									   observed_age_group.name AS age_group,&#xd;\n									   observed_age_group.sort_order AS sort_order&#xd;\n&#xd;\n				from obs o&#xd;\n						-- HTS CLIENTS WITH HIV STATUS BY SEX AND AGE&#xd;\n						 INNER JOIN patient ON o.person_id = patient.patient_id &#xd;\n						 AND o.concept_id = 2165&#xd;\n						 AND patient.voided = 0 AND o.voided = 0&#xd;\n						 AND o.obs_datetime BETWEEN CAST(:startDate AS DATE) AND CAST(:endDate AS DATE)&#xd;\n						 &#xd;\n						 -- PROVIDER INITIATED TESTING AND COUNSELING&#xd;\n						 AND o.person_id in (&#xd;\n							select distinct os.person_id &#xd;\n							from obs os&#xd;\n							where os.concept_id = 4228 and os.value_coded = 4227&#xd;\n							AND os.obs_datetime BETWEEN CAST(:startDate AS DATE) AND CAST(:endDate AS DATE)&#xd;\n							AND patient.voided = 0 AND o.voided = 0&#xd;\n						 )&#xd;\n						 &#xd;\n						 -- NEW TESTER, DOES NOT HAVE A HISTORY OF PREVIOUS TESTING&#xd;\n						 AND o.person_id in (&#xd;\n							select distinct os.person_id&#xd;\n							from obs os&#xd;\n							where os.concept_id = 2137 and os.value_coded = 2147&#xd;\n							AND os.obs_datetime BETWEEN CAST(:startDate AS DATE) AND CAST(:endDate AS DATE)&#xd;\n							AND patient.voided = 0 AND o.voided = 0&#xd;\n						 )&#xd;\n						 &#xd;\n						 INNER JOIN person ON person.person_id = patient.patient_id AND person.voided = 0&#xd;\n						 INNER JOIN person_name ON person.person_id = person_name.person_id&#xd;\n						 INNER JOIN patient_identifier ON patient_identifier.patient_id = person.person_id AND patient_identifier.identifier_type = 3&#xd;\n						 INNER JOIN reporting_age_group AS observed_age_group ON&#xd;\n						  CAST(:endDate AS DATE) BETWEEN (DATE_ADD(DATE_ADD(person.birthdate, INTERVAL observed_age_group.min_years YEAR), INTERVAL observed_age_group.min_days DAY))&#xd;\n						  AND (DATE_ADD(DATE_ADD(person.birthdate, INTERVAL observed_age_group.max_years YEAR), INTERVAL observed_age_group.max_days DAY))&#xd;\n				   WHERE observed_age_group.report_group_name = &apos;Modified_Ages&apos;) AS HTSClients_HIV_Status&#xd;\nORDER BY HTSClients_HIV_Status.HIV_Status, HTSClients_HIV_Status.Age)&#xd;\n) as hiv&#xd;\nWHERE HIV_Status = &apos;Negative&apos;</query>\n</org.openmrs.module.reporting.cohort.definition.SqlCohortDefinition>','2020-04-24 12:18:19',4,'2020-