tive&apos;</query>\n</org.openmrs.module.reporting.cohort.definition.SqlCohortDefinition>','2020-04-27 05:37:47',4,'2020-04-27 05:38:13',4,0,NULL,NULL,NULL,'ebf8843c-2e45-4156-8bbf-867273775513'),(472,'CITC New Positive','CITC New Positive','org.openmrs.module.reporting.indicator.Indicator','org.openmrs.module.reporting.indicator.CohortIndicator','org.openmrs.module.reporting.serializer.ReportingSerializer','<org.openmrs.module.reporting.indicator.CohortIndicator id=\"1\" uuid=\"512d2da0-391c-4ec8-b8cc-d9f3028bf456\" retired=\"false\">\n  <name>CITC New Positive</name>\n  <description>CITC New Positive</description>\n  <creator id=\"2\" uuid=\"c1c21e11-3f10-11e4-adec-0800271c1b75\"/>\n  <dateCreated id=\"3\">2020-04-27 05:39:50 UTC</dateCreated>\n  <changedBy reference=\"2\"/>\n  <dateChanged id=\"4\">2020-04-27 05:41:21 UTC</dateChanged>\n  <parameters id=\"5\">\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"6\">\n      <name>startDate</name>\n      <label>Start Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"7\">\n      <name>endDate</name>\n      <label>End Date</label>\n      <type>java.util.Date</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n    <org.openmrs.module.reporting.evaluation.parameter.Parameter id=\"8\">\n      <name>location</name>\n      <label>Location</label>\n      <type>org.openmrs.Location</type>\n      <required>true</required>\n    </org.openmrs.module.reporting.evaluation.parameter.Parameter>\n  </parameters>\n  <id>472</id>\n  <type>COUNT</type>\n  <cohortDefinition id=\"9\">\n    <parameterizable class=\"org.openmrs.module.reporting.cohort.definition.SqlCohortDefinition\" id=\"10\" uuid=\"9cd47068-2982-4ca1-a040-bc19ca313349\"/>\n    <parameterMappings id=\"11\">\n      <entry>\n      